<?php$skey = '0oo0OO0Oo'; //自定义密钥function cyjm($string = '', $skey = '') {	$strArr = str_split(base64_encode($string));	$strCount = count($strArr);	foreach (str_split($skey) as $key => $value) $key < $strCount && $strArr[$key].= $value;	return str_replace(array('=', '+', '/'), array('O0O0O', 'o000o', 'oo00o'), join('', $strArr));}$url = str_replace('$$$', '~~#', $url);$Arrstr1 = explode('#', $url);for ($k = 0;$k < count($Arrstr1);$k++) {	$vodfj = $Arrstr1[$k];	$lspo = strpos($vodfj, '$');	if($lspo>0){	$tname = substr($vodfj, 0, $lspo + 1);	$string = substr($vodfj, $lspo + 1);	}else{	$tname = '';	$string = $vodfj;	}	if (strstr($string, '~~')) {		$string = 'avavav'.cyjm(str_replace('~~', '', $string), $skey) . '$$$';	} else {		$string = 'avavav'.cyjm($string, $skey);	}	$jmstr.= $tname . $string . '#';}$jmstr = substr(str_replace('$$$#', '$$$', $jmstr), 0, -1);$url = "'" . $jmstr . "';"; ?>